function inflowplot(shade,low,high,choplow,chophigh)% close allclc;set(0, 'DefaultAxesFontName', 'Helvetica');set(0, 'DefaultAxesFontSize', 14);d2r=pi/180;if nargin==0    shade=0;end% % Loads the .data file from the helium run directory.% load("VBZ_PSI.dat")rad=1.676;om = 124.6;% asr=3*pi/180;speed = [10 20 40 50 60 80 100];figcount = 1;% for i = 1:length(speed)        % filename = ['C:\Users\hauti\heli-fwmrtr2\MyCasesNew-8turns\TR\t' num2str(speed(i)) '\VBZ_PSI.DAT']    % VBZ_PSI = load("VBZ_PSI2.dat")    % filename = 'C:\Users\hauti\MATLAB Drive\Research Project\VBZ_PSI2.dat';    VBZ_PSI = load("VBZ_PSIt100.dat")    % VBZ_PSI = VBZ_PSI(1153:end,:);    k=0;    for p=1:36        for s=1:32            k=k+1;            cpx(s,p)=VBZ_PSI(k,1)-10.15138;            cpy(s,p)=VBZ_PSI(k,2)+0.36401;            cpz(s,p)=VBZ_PSI(k,3)-0.25194;            inflow(s,p)=VBZ_PSI(k,7); % Inflow without the induced contribution of the near wake in the seventh column            inflownw(s,p)=VBZ_PSI(k,6); % Inflow in the z-direction            rloc(s,p)=cpx(s,p)/rad;         end    end            r=rloc(:,1)';        psi=(0:10:360)*d2r;        [th,r]=meshgrid(psi,r);    [x,y]=pol2cart(th,r);        inflownw(:,37)=inflownw(:,1);    inflownw=-inflownw/(om*rad);        for p=1:37        for s=1:32            if nargin >=5                if(inflownw(s,p)<choplow)                        inflownw(s,p)=choplow;                end                if(inflownw(s,p)>chophigh)                    inflownw(s,p)=chophigh;                end            end        end    end            if nargin >= 3       V=[low high];     end            % figure    h=polar([0 2*pi],[0,1]);    delete(h)    hold on    % if shade == 2    %    colormap(gray)    % end    % if nargin >= 3    %    caxis(V)    % end       % contourf(x,y,inflow,10)    % %title('Freewake')    % colorbar    % if shade==2    %     print -depsc inflow-nonw-gray.eps    % else    %     print -depsc inflow-nonw.eps    % end        figure(2)    % h=polar([0 2*pi],[0,1]);    % delete(h)    % hold on    if shade == 2    %   colormap(gray)        colormap(bone)    end    if nargin >= 3       caxis(V)    end    levels = linspace(min(min(inflownw)),max(max(inflownw)),20);    [c,h]=contourf(x,y,inflownw,levels);    %clabel(c,h)    %title('Freewake')    colorbar    % if shade==2     %     print -depsc inflow-gray.eps    % else    %     print -depsc inflow.eps    % end    figcount = figcount + 1;% end